mapper.select:
SELECT * FROM products

     AND status = #{status} 
     AND category = #{category} 
     AND name LIKE concat('%', #{keyword}, '%') 

ORDER BY create_time DESC, id DESC

mapper.insert:
INSERT INTO products(
    name, image, description, points_needed, status,
    category, stock, create_time, update_time
) VALUES (
             #{name}, #{image}, #{description}, #{pointsNeeded}, #{status},
             #{category}, #{stock}, #{createTime}, #{updateTime}
         )

mapper.update:
UPDATE products
name = #{name}, image = #{image}, description = #{description}, points_needed = #{pointsNeeded}, status = #{status}, category = #{category}, stock = #{stock}, update_time = #{updateTime},
WHERE id = #{id}
